import React, { useState, useEffect } from 'react';
import $ from 'jquery';
import DataTable from 'datatables.net-dt';
import './App.css';
import * as XLSX from 'xlsx';

const App = () => {
  const [tableData, setTableData] = useState([
    { subject: 'TA310', Age: 50, Sex: 'F', OnsetAge: 26, Handedness: 'R', DomHemi: 'B', Wada: '-', fMRI: 'B', CSM: '', SurgHemi: 'R', SurgType: 'ATL+AH'},
    { subject: 'TA311', Age: 62, Sex: 'F', OnsetAge: 25, Handedness: 'R', DomHemi: 'L', Wada: '-', fMRI: 'L', CSM: '', SurgHemi: 'L', SurgType: 'ATL+AH+Other'},
    { subject: 'TA312', Age: 43, Sex: 'F', OnsetAge: 17, Handedness: 'R', DomHemi: 'R', Wada: 'R', fMRI: 'R', CSM: '', SurgHemi: 'L', SurgType: 'ATL+AH'},
    { subject: 'TA313', Age: 44, Sex: 'F', OnsetAge: '', Handedness: 'R', DomHemi: 'L', Wada: '-', fMRI: 'L', CSM: '', SurgHemi: 'L', SurgType: 'Other'},
    { subject: 'TA314', Age: '', Sex: '', OnsetAge: '', Handedness: '', DomHemi: '', Wada: '', fMRI: '', CSM: '', SurgHemi: '', SurgType: ''},
  ]);
  const [columns, setColumns] = useState(['subject', 'Age', 'Sex', 'OnsetAge', 'Handedness', 'DomHemi', 'Wada', 'fMRI', 'CSM', 'SurgHemi', 'SurgType']);

  useEffect(() => {
    const table = new DataTable('#example');

    return () => {
      table.destroy();
    };
  }, [tableData]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];

    const reader = new FileReader();
    reader.onload = (e) => {
      const data = new Uint8Array(e.target.result);
      const workbook = XLSX.read(data, { type: 'array' });

      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];

      const jsonData = XLSX.utils.sheet_to_json(worksheet, { defval: '' });
      updateTableData(jsonData);
    };

    reader.readAsArrayBuffer(file);
  };

  const updateTableData = (excelData) => {
    setTableData((prevData) => {
      const updatedData = [...prevData];

      excelData.forEach((excelRow) => {
        const index = updatedData.findIndex((row) => row.subject === excelRow.subject);

        if(index !== 1) {
          updatedData[index] = { ...updatedData[index], ...excelRow};
        } else {
          updatedData.push(excelRow);
        }
      });

      return updatedData
    });
  };

  return (
    <div className="container">
      <link rel="stylesheet" href="https://cdn.datatables.net/2.1.5/css/dataTables.dataTables.css" />
      <script src="https://cdn.datatables.net/2.1.5/js/dataTables.js"></script>
      <h1>Patient DataTable</h1>

      <input type="file" accept=".xlsx, .xls" onChange={handleFileUpload} />
        <table id="example" class="display">
          <thead>
            <tr>
              {columns.map((col, index) => (
                <th key={index}>{col}</th>
              ))}
              </tr>
          </thead>
          <tbody>
            {tableData.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {columns.map((col, colIndex) => (
                  <td key={colIndex}>{row[col]}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
    </div>
  );
};

export default App;
