import $ from 'jquery';

let counter = 1;

export const handleAddRow = (table) => {
    const addNewRow = () => {
        table.row
            .add([
                counter + 'New Subject',
                counter + ' ',
                counter + ' ',
                counter + ' ',
                counter + ' ',
                counter + ' ',
                counter + ' ',
                counter + ' ',
                counter + ' ',
                counter + ' ',
                counter + ' ',
            ])
            .draw(false);
        counter++;
    };

    $('#addRow').on('click', addNewRow);
    addNewRow();
};
