import $ from 'jquery';

export const handleEditRow = (table) => {
    $('#example').on('click', 'tbody tr', function () {
        table.row(this).edit({
            buttons: [
                {
                    label: 'Cancel',
                    fn: function() {
                        this.close();
                    }
                },
                'Edit'
            ]
        });
    });
};
