import $ from 'jquery';

export const handleAddRow = (table) => {
    const addNewRow = () => {
        table.row
            .add([
                'New Subject',
                ' ',
                ' ',
                ' ',
                ' ',
                ' ',
                ' ',
                ' ',
                ' ',
                ' ',
                ' ',
            ])
            .draw(false);
    };
    $('#addRow').off('click').on('click', addNewRow);
};
