import React, { useEffect } from 'react';
import $ from 'jquery';
import 'datatables.net';
import TableContent from './components/TableContent'
import { handleAddRow } from './components/addRow';
import { handleExcelUpload } from './components/uploadExcel';
import { handleExcelExport } from './components/exportExcel';


const App = () => {
  useEffect(() => {
    const table = $('#example').DataTable();

    handleAddRow(table);
    handleExcelUpload(table);
    handleExcelExport(table);

    $('#example tbody').on('click', 'tr', function () {
      if ($(this).hasClass('selected')) {
        $(this).removeClass('selected');
      } else {
        table.$('tr.selected').removeClass('selected');
        $(this).addClass('selected');
      }
    });

    $('#buttonDelete').on('click', function () {
      table.row('.selected').remove().draw(false);
    });

    $('#buttonEdit').on('click', function () {
      const selectedRow = table.row('.selected');
      if (selectedRow.node()) {
        const rowData = selectedRow.data();
        $(selectedRow.node()).find('td').each(function (index) {
          const cellContent = $(this).text() || '';
          $(this).html(`<input type="text" value="${cellContent}" class="editable-input">`);
        });
      } else {
        alert('Please select a row to edit.');
      }
    });

    $('#example').on('blur', '.editable-input', function () {
      const cell = table.cell($(this).parent());
      const newValue = $(this).val() || '';
      cell.data(newValue).draw();
    });

    $('#example').on('keydown', '.editable-input', function (e) {
      if (e.key === 'Enter') {
        $(this).blur();
      }
    });

    $('#uploadButton').on('click', function () {
      $('#uploadExcel').click();
    });

    return () => {
      $('#example tbody').off('click', 'tr');
      $('#buttonDelete').off('click');
      $('#buttonEdit').off('click');
      $('#addRow').off('click');
      $('#uploadButton').off('click');
      $('#example').off('blur', '.editable-input');
      $('#example').off('keydown', '.editable-input');
    };
  }, []);


  return (
    <html>
      <head>
        <link href="https://nightly.datatables.net/css/jquery.dataTables.css" rel="stylesheet" type="text/css" />
        <script src="https://nightly.datatables.net/js/jquery.dataTables.js"></script>
        <title>Patient DataTable</title>
      </head>
      <body>
        <div class="container">
          <p><button id="addRow">Add new row</button></p>
          <p><button id="buttonEdit">Edit Row</button></p>
          <p><button id="buttonDelete">Delete selected row</button></p>
          <TableContent />
          <p><button id="exportButton">Export Excel</button></p>
    </div>
  </body>
</html>
  );
};

export default App;
